  width: 100vw;
  overflow: hidden;
}

h1 {
  font-family: 'Roca', sans-serif;
  font-size: 3rem;
  letter-spacing: 0.25rem;
  margin: 0;
  line-height: 3rem;
  text-align: left;
}

h2 {

  font-size: 2rem;
  padding: 0px;
  margin: 0px 0px 1rem;
  font-family: 'Roca', sans-serif;
  line-height: 3rem;
  letter-spacing: -0.05rem;
  font-weight: 900;
}

h3 {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 0.5rem;
  font-weight: normal;
  margin-top: 0px;
}

h3 span {
  color: #fff;
  padding: 0.2rem 0.5rem;
  border-radius: 0.5rem;
  text-shadow: 0 1px #222;
}

nav {
  display: flex;
  justify-content: center;
  flex-wrap: wrap;
  gap: 0.5rem;
  margin-bottom: 2rem;
}

button {
  padding: 10px 18px;
  border-radius: 0.5rem;
  color: #222;
  font-size: 16px;
  text-shadow: 0 1px #fff;
  cursor: pointer;
  border: 2px solid #fff;
}


nav button {
  color: #fff;
  font-size: 16px;
  text-shadow: 0 1px #222;
}

button:hover {
  border: 2px solid #222;
}


section {
  display: none;
  padding: 1rem;
}


/* Layout for sidebar and content */

.main-layout {
  display: flex;
  height: 100vh;
  width: 100vw;
  position: relative;
}


.sidebar {
  width: 320px;
  min-width: 260px;
  max-width: 400px;
  background: #f7f6f2;
  padding: 2rem 1rem 1rem 1rem;
  box-sizing: border-box;
  border-right: 1px solid #e0dfdb;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  position: relative;
  height: 100vh;
  z-index: 2;
  flex-shrink: 0;
}

.sidebar h2 {
  margin-bottom: 2rem;
  text-align: left;
}

.sidebar .filters {
  margin-bottom: 2rem;
  text-align: left;
}


.content-area {
  flex: 1 1 0;
  overflow-y: auto;
  padding: 2rem 2vw;
  box-sizing: border-box;
  height: 100vh;
  min-width: 0;
}

.places-list {
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
}


/* ---- .listing ---- */

.listing {
  padding: 1rem;
}

.listing>button {
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: start;
  width: 100%;
  gap: 1rem;
  background: rgba(255, 255, 255, 0.8);
