.listing>button .open {
  opacity: 0;
  width: 1.5rem;
}

.listing>button:hover .open {
  opacity: 0.25;
}

.listing img {
  width: 75px;

}

.listing .profile img {
  align-self: center;
  width: 300px;
  max-width: 30vw;
}

[popover] {
  padding: 2rem 2rem 3rem;
  border-radius: 1rem;
  border: none;
  filter: drop-shadow(0 0 2rem rgba(0, 0, 0, 0.3));
}

[popover]::backdrop {
  backdrop-filter: blur(3px);
}


[popover] .profile {
  display: flex;
  flex-direction: column;
  align-items: center;
  max-width: 75vw;
}

fieldset {
  border: none;
}

footer {
  padding: 2rem;
}

header img {
  width: 80px;
  padding: 1.2rem 0.8rem 0;
}


header {
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 2rem;
}

button.close {
  align-self: end;
  position: relative;
  z-index: 10;
  /* ensure that the button is on top of the subsequent text */
  cursor: pointer;
}

.details {
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
  width: 100%;
  justify-content: space-between;
  align-items: center;
}




.stats {
  margin-top: 1rem;
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  gap: 0.5rem;
}


