  .details {
    flex-direction: row;
    gap: 2rem;
  }

  .stats {
    gap: 2rem;
    margin-top: 2rem;
  }
}

.stat {
  width: 4rem;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 0.25rem;
}

.base-stat {
  width: 3rem;
  height: 3rem;
  background: rgba(0, 0, 0, 0.3);
  color: #fff;
  font-weight: bold;
  font-size: 1.5rem;
  border-radius: 3rem;
  display: flex;
  justify-content: center;
  align-content: center;
  flex-direction: column;

}

.stat-name {
  font-weight: bold;
  color: rgba(0, 0, 0, 0.5);
}


