.grass .listing {
  background: #78c850
}

.poison,
.poison .listing {
  background: #a040a0
}

.electric,
.electric .listing {
  background: #f8d030
}

.ground,
.ground .listing {
  background: #e0c068
}

.psychic,
.psychic .listing {
  background: #f85888
}

.rock,
.rock .listing {
  background: #b8a038
}

.ice,
.ice .listing {
  background: #98d8d8
}

.bug,
.bug .listing {
  background: #a8b820
}

.dragon,
.dragon .listing {
  background: #7038f8
}

.ghost,
.ghost .listing {
  background: #705898
}

.dark,
.dark .listing {
  background: #705848
}

.steel,
.steel .listing {
  background: #b8b8d0
}

.fairy,
.fairy .listing {
  background: #ee99ac
}

.place-item {
  padding: 1rem;
  background-color: #fffffa;
  border-radius: 1rem;
  box-shadow: 0 2px 8px rgba(0,0,0,0.04);
  transition: background 0.2s;
}

.place-item:hover {
  background-color: #eeeee8;
}

