html, body {
  height: 100%;
  width: 100%;
  margin: 0;
  padding: 0;
}

body {
  font-family: 'TwentiethCenturyMedium', sans-serif;
  margin: 0;
  background-color: rgb(240, 238, 234);
  height: 100vh;
  width: 100vw;
  overflow: hidden;
}

h1 {
  font-family: 'Roca', sans-serif;
  font-size: 3rem;
  letter-spacing: 0.25rem;
  margin: 0;
  line-height: 3rem;
  text-align: left;
