/* Filter tags for sidebar */
.filter-tag {
  display: inline-block;
  background: #e0dfdb;
  color: #222;
  border-radius: 1rem;
  padding: 0.4em 1em;
  margin: 0.2em 0.3em 0.2em 0;
  font-size: 1rem;
  cursor: pointer;
  transition: background 0.2s, color 0.2s, box-shadow 0.2s;
  border: 2px solid transparent;
  box-shadow: none;
}
.filter-tag:hover {
  background: #d1cfc7;
  color: #111;
  box-shadow: 0 2px 8px rgba(0,0,0,0.07);
}
.filter-tag.active {
  background: #222;
  color: #fff;
  border-color: #222;
  box-shadow: 0 2px 12px rgba(0,0,0,0.12);
}

html, body {
