  font-size: 16px;
  text-shadow: 0 1px #222;
}

button:hover {
  border: 2px solid #222;
}


section {
  padding: 1rem;
}



/* Layout for sidebar and content */


.main-layout {
  height: 100vh;
  width: 100vw;
}




.sidebar {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  width: 50vw;
  min-width: 50vw;
  max-width: 50vw;
  background: #f7f6f2;
  padding: 2rem 1rem 1rem 1rem;
  box-sizing: border-box;
  border-right: 1px solid #e0dfdb;
  position: fixed;
  left: 0;
  top: 0;
  height: 100vh;
  z-index: 10;
}

.sidebar h2 {
  margin-bottom: 2rem;
  text-align: left;
}

.sidebar .filters {
  margin-bottom: 2rem;
  text-align: left;
}




.content-area {
  display: block;
  margin-left: 50vw;
  width: 50vw;
  overflow-y: auto;
  padding: 2rem 2vw;
  box-sizing: border-box;
  height: 100vh;
  min-width: 0;
}

.places-list {
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
}


/* ---- .listing ---- */

.listing {
  padding: 1rem;
}

.listing>button {
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: start;
  width: 100%;
  gap: 1rem;
  background: rgba(255, 255, 255, 0.8);
}

.listing>button .open {
  opacity: 0;
  width: 1.5rem;
}

.listing>button:hover .open {
  opacity: 0.25;
}

.listing img {
  width: 75px;

}

.listing .profile img {
  align-self: center;
  width: 300px;
  max-width: 30vw;
}

[popover] {
  padding: 2rem 2rem 3rem;
  border-radius: 1rem;
  border: none;
  filter: drop-shadow(0 0 2rem rgba(0, 0, 0, 0.3));
}

[popover]::backdrop {
