}


[popover] .profile {
  display: flex;
  flex-direction: column;
  align-items: center;
  max-width: 75vw;
}

fieldset {
  border: none;
}

footer {
  padding: 2rem;
}

header img {
  width: 80px;
  padding: 1.2rem 0.8rem 0;
}


header {
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 2rem;
}

button.close {
  align-self: end;
  position: relative;
  z-index: 10;
